import { ColumnDefinition } from 'griddle-react';

const GriddleColumnDefinitionProvider = (filters) => {
  const cols = filters.map(filter => {
    /* Create custom type structures here */
    return <ColumnDefinition id={filter.name} />;
  });
  return <ColumnDefinition>{cols}</ColumnDefinition>;
};

export default GriddleColumnDefinitionProvider;
